<template>
    <div class="shareLinks">

        <div v-if="isSet('clipboard')" class="vca-input" >
            <input type="text" disabled="disabled" id="shareURL" :value="share.url">
            <button class="vca-button" @click.stop.prevent="copyLink">Link kopieren</button><br/><br/>
        </div>

        <button v-if="isSet('whatsapp')" class="vca-button whatsapp" @click.stop.prevent="shareWhatsApp">
            <img title="Via WhatsApp teilen" src="data:image/png;base64," />
            <div>Per WhatsApp teilen</div>
        </button>
        <button v-if="isSet('telegram')" class="vca-button telegram" @click.stop.prevent="shareTelegram">
            <img title="Via Telegram teilen" src="data:image/png;base64," />
            <div>Per Telegram teilen</div>
        </button>
        <button v-if="isSet('twitter')" class="vca-button twitter" @click.stop.prevent="shareTwitter">
            <img title="Via Twitter teilen" src="data:image/png;base64," />
            <div>Per Twitter teilen</div>
        </button>
        <button v-if="isShareMobile && isSet('device')" class="vca-button mobile" @click.stop.prevent="shareMobile">
            <img title="Einladung teilen" src="data:image/png;base64," />
            <div>Einladung teilen</div>
        </button>
    </div>
</template>
<script>

export default {
    name: 'VcaShareButtons',
    props: {
        share: {
            type: Object,
            default: function() {
                return {
                    url: "",
                    text: "",
                    media: [ "clipboard", "whatsapp", "telegram", "twitter", "device" ]
                }
            }
        }
    },
    computed: {
        isShareMobile() {
            return navigator.share !== undefined
        }
    },
    methods:{
        isSet(val) {
            return this.share.media.includes(val)
        },
        shareWhatsApp() {
            window.location = "whatsapp://send?text=" + this.share.text + " " + encodeURIComponent(this.share.url)
        },
        shareTelegram() {
            window.location = "https://t.me/share/url?url=" + encodeURIComponent(this.share.url) + "&text=" + this.share.text + ""
        },
        shareTwitter() {
            window.location = "https://twitter.com/intent/tweet?text=" + this.share.text + "&url=" + encodeURIComponent(this.share.url)
        },
        shareMobile() {
            console.log(navigator.share)
            if (navigator.share) {
                navigator.share({
                    title: "",
                    text: this.share.text,
                    url: this.share.url
                }).then(() => console.log('Successful share'))
                .catch(error => console.log('Error sharing:', error));
            }
        },
        copyLink () {
            let link = document.querySelector('#shareURL')
            link.disabled = false
            link.select()

            try {
                document.execCommand('copy');
                console.log('Successful copy')
            } catch (error) { console.log('Error copy:', error) }

            window.getSelection().removeAllRanges()
            link.disabled = true
        }
    }

}
</script>
<style scoped>

    .whatsapp, .telegram, .twitter, .mobile {
        display: flex;
    }

    .whatsapp img, .telegram img, .twitter img, .mobile img {
        width: 35px;
    }
    .whatsapp div, .telegram div, .twitter div, .mobile div {
        width: inherit;
    }

    .whatsapp img, .whatsapp div,
    .telegram img, .telegram div,
    .twitter img, .twitter div,
    .mobile img, .mobile div {
        margin: auto;
    }

    .shareLinks {
        display: none;
    }

    @media only screen and (max-width: 500px) {
        .shareLinks {
            display: block;
        }
    }

</style>
